import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate, useParams } from 'react-router-dom';

function UpdateEmployee() {
    const [employee, setEmployee] = useState({
        first_name: '',
        last_name: '',
        email: '',
        gender: '',
        salary: '',
    });
    const { id } = useParams();
    const navigate = useNavigate();

    useEffect(() => {
        const fetchEmployee = async () => {
            try {
                const response = await axios.get(`http://localhost:3000/api/v1/employees/${id}`);
                setEmployee(response.data);
            } catch (error) {
                console.error('Error fetching employee:', error);
            }
        };

        fetchEmployee();
    }, [id]);

    const handleChange = (e) => {
        setEmployee({ ...employee, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            // Submit form
        } catch (error) {
            console.error('Error updating employee:', error);
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <label>
                First Name:
                <input name="first_name" value={employee.first_name} onChange={handleChange} />
            </label>
            <label>
                Last Name:
                <input name="last_name" value={employee.last_name} onChange={handleChange} />
            </label>
            <label>
                Email:
                <input name="email" value={employee.email} onChange={handleChange} />
            </label>
            <label>
                Gender:
                <input name="gender" value={employee.gender} onChange={handleChange} />
            </label>
            <label>
                Salary:
                <input name="salary" value={employee.salary} onChange={handleChange} />
            </label>
            <button type="submit">Save</button>
            <button type="button" onClick={handleCancel}>Cancel</button>
        </form>
    );
}

export default UpdateEmployee;
