import React, { useContext } from 'react';
import { BrowserRouter as Router, Route, Routes, Link, NavLink } from 'react-router-dom';
import './App.css';
import SignUp from './components/SignUp'; 
import Login from './components/Login';
import { AuthContext } from './components/AuthContext'; // Import the AuthContext

function App() {
  const { isLoggedIn, logOut } = useContext(AuthContext); // Use the AuthContext

  return (
    <Router>
      <div className="App">
        <header className="App-header">
          <h1>Employee Management App</h1>
          <nav>
            {/* Conditionally render links based on authentication status */}
            {!isLoggedIn && <Link to="/">Sign Up</Link>}
            {!isLoggedIn && <> | <Link to="/login">Login</Link></>}
            {isLoggedIn && <NavLink to="/" onClick={logOut}>Logout</NavLink>}
          </nav>
        </header>
        <main>
          <Routes>
            <Route path="/" element={<SignUp />} />
            <Route path="/login" element={<Login />} />
          </Routes>
        </main>
      </div>
    </Router>
  );
}

export default App;
